<template>
 <div class="page" :style="{background: activeColor}">
<div id="root" class="container" >
    <div class="m-artical-action">
        <div class="artical-action-mid" id="action_mid" @click="Readinginteractive"></div>
    </div>
   
    <div id="fition_chapter_title"></div>
    <div id="fiction_container" class="m-read-content" :style="{fontSize: fontsize}"><h4>{{chapter.title}}</h4>
    
    <text>
        {{chapter.content}}
    </text>
    </div>
    
    
    <div id="" class="m-button-bar">
        <ul class="u-tab">
            <li id="prev_button" @click="lastChapter(chapter.previd)">上一章</li>
            <li id="next_button" @click="nextChapter(chapter.nextid)">下一章</li>
        </ul>
    </div>
    <div class="nav-pannel-bk font-container"  v-show="fontempty"></div>
