            <button id="small-font" class="font-size-button" @click="Font('small')">小</button>
        </div>
        <div class="child-mod">
            <span>背景</span>
            <div id="first_day" class="bk-container bk-container-white"  @click="Current(1,'#f7eee5')">
                <div class="bk-container-current" :class="{ishide1}" ></div>
            </div>
            <div class="bk-container bk-container-paple"  @click="Current(2,'#e9dfc7')">
                <div class="bk-container-current"  :class="{ishide2}" ></div>
            </div>
            <div class="bk-container bk-container-grey"  @click="Current(3,'#a4a4a4')">
                <div class="bk-container-current" :class="{ishide3}" ></div>
            </div>
            <div class="bk-container bk-container-green"   @click="Current(4,'#cdefce')">
                <div class="bk-container-current" :class="{ishide4}"></div>
            </div>
            <div id="last_night" class="bk-container bk-container-blue"  @click="Current(5,'#283548')">
                <div class="bk-container-current" :class="{ishide5}"></div>
            </div>
        </div>
    </div>

    <div class="bottom-nav-bk bottom_nav" v-show="empty"></div>
    <div class="bottom-nav bottom_nav" v-show="empty">
        <div class="item" id="menu_button">
             <a :href="'/pages/catalog/main?bookid='+ bookid +''"   style="height:100%">
                <div class="item-warp">
                    <div class="icon icon-menu"></div>
                    <div class="icon-text">
                        目录
                    </div>
                </div>
             </a>
        </div>
        <div class="item" id="font-button" @click="Fontactive">
            <div class="item-warp">
                <div class="icon icon-ft"></div>
                <div class="icon-text">
                    字体
                </div>
            </div>
        </div>
        <div class="item" id="night-day-button" @click="Dayandnight">
            <div class="item-warp"  id="day_icon"   v-show="Day">
                <div class="icon icon-day"></div>
                <div class="icon-text">
                    白天
                </div>
            </div>
            <div class="item-warp"  id="night_icon"  v-show="night">
                <div class="icon icon-night"></div>
                <div class="icon-text">
                    夜间
                </div>
            </div>
        </div>
    </div>

</div>
  </div>
</template>
 
<script>

import {get,showModal} from '@/utils'

export default {
    data () {
      return {
        bookid: '',
        catalogid:'',
        chapter : {},
        empty:false,
        fontempty:false,
        Day:true,
        night:false,
        activeColor : '',
        ishide1 :false,
        ishide2 :false,
        ishide3 :false,
        ishide4 :false,
        ishide5 :false,
        fontsize : ''
      }
    },
 
    onPageScroll () {
        this.empty = false;
        this.fontempty = false;
    },
    methods: {
       Readinginteractive() {
           if(this.empty===false){this.empty = true;}else{this.empty = false;}
           this.fontempty = false;
       },
       Fontactive(){
           if(this.fontempty===false){this.fontempty = true;}else{this.fontempty = false;}
       },
       Dayandnight(){
           if(this.Day===true){
               this.Day = false;
               this.night = true;
               this.activeColor = '#283548';
           }else{
               this.Day = true;
               this.night = false;
               this.activeColor = '#fff';
           }
           wx.setStorageSync('sp_activeColor', this.activeColor)
           wx.setStorageSync('sp_Day', this.Day)
           wx.setStorageSync('sp_night', this.night)
       },
       Current(index,color){
           this.ishide1=false
           this.ishide2=false
           this.ishide3=false
           this.ishide4=false
           this.ishide5=false
           this.activeColor = color
           if(index === 1){
                this.ishide1 = true
           }else if(index === 2){
                this.ishide2 = true
           }else if(index === 3){
                this.ishide3 = true
           }else if(index === 4){
                this.ishide4 = true
           }else if(index === 5){
                this.ishide5 = true
           }
           wx.setStorageSync('sp_activeColor', color)
       },
       Font(type){
           var size = parseInt(this.fontsize)
           if(type === 'large'){
               size +=1
           }else if(type === 'small'){
               size -=1
           }
           if(size>=20){
                return false;
           }else if(size<=10){
                return false;
           }
           this.fontsize = size + 'px'
           wx.setStorageSync('sp_fontsize', size + 'px')
       },
       async getChapter(bookid){
            
            if(!this.catalogid){
                var res = await get('/weapp/chapterdetails',{
                    bookid : bookid,
                    startid : 'first'
                })
            }else{
                var res = await get('/weapp/chapterdetails',{
                    bookid : bookid,
                    catalogid : this.catalogid
                })
            }
           
            this.chapter = res.data
            
       },

       async nextChapter(nextid){

            if(nextid==='最后一章'){
                 return showModal('提示', `已经是最后一章啦`)
            }
            wx.setStorageSync('sp_catalogid'+this.bookid, nextid)
            const res = await get('/weapp/chapterdetails',{
                bookid : this.bookid,
                catalogid : nextid
            })

            this.chapter = res.data
            wx.pageScrollTo({
                scrollTop: 0
            })
       },

       async lastChapter(previd){
            if(previd==='起始章节'){
                 return showModal('提示', `已经是第一章啦`)
            }
            const res = await get('/weapp/chapterdetails',{
                bookid : this.bookid,
                catalogid : previd
            })
            wx.setStorageSync('sp_catalogid'+this.bookid, previd)
            this.chapter = res.data
            wx.pageScrollTo({
                scrollTop: 0
            })   
       }
    },
    onLoad (options) {
            this.bookid = options.id
            var catalogid = wx.getStorageSync('sp_catalogid'+options.id)
            if(!catalogid){
                 this.catalogid = options.catalogid
                 wx.setStorageSync('sp_catalogid'+options.id, options.catalogid)
            }else if(options.action==='catalog'){
                 this.catalogid = options.catalogid
                 wx.setStorageSync('sp_catalogid'+options.id, options.catalogid)
            }else{
                this.catalogid = catalogid
            }
	},
    
    mounted () {
         var fontsize = wx.getStorageSync('sp_fontsize')
         var activeColor = wx.getStorageSync('sp_activeColor')
         var sp_Day = wx.getStorageSync('sp_Day')
         var sp_night = wx.getStorageSync('sp_night')
         if(fontsize){
             this.fontsize = fontsize
         }else{
            this.fontsize ='14px'
         }
         if(activeColor){
             this.activeColor = activeColor
         }else{
             this.activeColor ='#fff'
         }
         
         if(sp_Day){
             this.Day = sp_Day
             this.night = false
         }
         if(sp_night){
             this.night = sp_night
             this.Day = false
         }
         this.getChapter(this.bookid)
    } 

}
</script>
